mapper.update:
UPDATE `financail_statement`
SET
code = #{code} ,
date = #{date}
,pe = #{pe}
,bvps = #{bvps}
,cps = #{cps}
,roe = #{roe}
,jroe = #{jroe}
,sgpr = #{sgpr}
,smpr = #{smpr}
,dtar = #{dtar}
,opgr = #{opgr}
,toi = #{toi}
,toc = #{toc}
,oi = #{oi}
,oc = #{oc}
,op = #{op}
,tp = #{tp}
,mp = #{mp}
,mpbpc = #{mpbpc}
,ta = #{ta}
,tl = #{tl}
,se = #{se}
,tacf = #{tacf}
,iacf = #{iacf}
,facf = #{facf}
,cnca = #{cnca}
WHERE
code = #{code} And
date = #{date}

mapper.select:
SELECT

FROM `financail_statement`
WHERE 1=1
AND code = #{code}
AND date = #{date}
AND pe = #{pe}
AND bvps = #{bvps}
AND cps = #{cps}
AND roe = #{roe}
AND jroe = #{jroe}
AND sgpr = #{sgpr}
AND smpr = #{smpr}
AND dtar = #{dtar}
AND opgr = #{opgr}
